mapper.select:
		 SELECT MEMBER_NO, ID, PW, NAME, EMAIL, ADDRESS, ADDR_DETAIL, PHONE, AGREE_STATE, SIGN_IN, PW_MODIFIED, INFO_MODIFIED, SESSION_ID, SESSION_LIMIT
       	   FROM MEMBER
       LIMIT #{beginRecord}, #{recordPerPage}

SELECT MEMBER_NO, ID, PW, NAME, EMAIL, ADDRESS, ADDR_DETAIL, PHONE, AGREE_STATE, SIGN_IN, PW_MODIFIED, INFO_MODIFIED, SESSION_ID, SESSION_LIMIT
  FROM MEMBER
 WHERE MEMBER_NO = #{memberNo}

mapper.update:
UPDATE
       MEMBER
   SET
       NAME = #{name}
     , EMAIL = #{email}
     , ADDRESS = #{address}
     , ADDR_DETAIL = #{addrDetail}
     , PHONE = #{phone}
     , INFO_MODIFIED = NOW()
 WHERE
   	   MEMBER_NO = #{memberNo}
