mapper.select:
SELECT *
  FROM NOTICE
   ORDER BY NOTICE_NO DESC LIMIT #{beginRecord}, #{endRecord}

SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_DATE, NOTICE_HIT
  FROM NOTICE
 WHERE NOTICE_NO = #{noticeNo}

mapper.update:
UPDATE
       NOTICE
   SET
       NOTICE_TITLE = #{noticeTitle}
     , NOTICE_CONTENT = #{noticeContent}
     , NOTICE_DATE = NOW()
 WHERE
   	   NOTICE_NO = #{noticeNo}
