mapper.select:
SELECT COUNT(*)
FROM PRODUCT_QTY
WHERE PRO_NO = #{proNo}
AND	PRO_SIZE = #{proSize}

SELECT PROIMG_NO, PROIMG_NAME, PROIMG_PATH, PROIMG_ORIGIN, PRO_NO
  FROM PRODUCT_IMAGE
 WHERE  PROIMG_NO = #{proimgNo}

SELECT PROIMG_NO, PROIMG_NAME, PROIMG_PATH, PROIMG_ORIGIN, PRO_NO
  FROM PRODUCT_IMAGE
 WHERE PRO_NO = #{proNo}

mapper.update:
UPDATE PRODUCT
   SET PRO_NAME = #{proName}, PRO_PRICE= #{proPrice}, PRO_DETAIL= #{proDetail}, PRO_DATE = SYSDATE()
 WHERE PRO_NO = #{proNo}

mapper.select:
SELECT PRO_NO, PRO_QTY ,PRO_DISCOUNT, PRO_SIZE
FROM PRODUCT_QTY
WHERE PRO_NO = #{proNo}
AND PRO_SIZE = #{proSize}

mapper.update:
UPDATE PRODUCT_QTY
SET PRO_QTY = #{proQty} ,PRO_DISCOUNT = #{proDiscount}
WHERE PRO_NO = #{proNo}
AND PRO_SIZE = #{proSize}

mapper.select:
SELECT * FROM REVIEW
WHERE PRO_NO = #{proNo}
ORDER BY REVIEW_NO DESC
LIMIT #{beginRecord}, #{recordPerPage}
