mapper.select:
SELECT *
  FROM QNA
ORDER BY QNA_GROUP_NO DESC LIMIT #{beginRecord}, #{endRecord}

SELECT QNA_NO, ID, QNA_TITLE, QNA_CONTENT, QNA_DATE
  FROM QNA
 WHERE QNA_NO = #{qnaNo}

mapper.update:
UPDATE QNA
   SET QNA_TITLE = #{qnaTitle}, QNA_CONTENT = #{qnaContent}, QNA_DATE = NOW()
 WHERE QNA_NO = #{qnaNo}

UPDATE QNA
   SET QNA_STATE = -1
 WHERE QNA_GROUP_NO = #{qnaGroupNo}
